MagentoApp.module("uma_website", function(module, app, Backbone, Marionette, $j, _) {

    var LayoutView = Marionette.LayoutView,
        _super = LayoutView.prototype;

    var PageProductDetailsView = LayoutView.extend({

        initialize: function(options) {
            _super.initialize.apply(this, arguments);
            this.bindTabToggle();
        },
        /**
         * support tab toggle to see Product Infomation
         **/
        bindTabToggle: function() {

            var self = this;
            $j('.toggle-content').each(function () {
                var wrapper = jQuery(this);

                var hasTabs = wrapper.hasClass('tabs');
                var hasAccordion = wrapper.hasClass('accordion');
                var startOpen = wrapper.hasClass('open');

                var dl = wrapper.children('dl:first');
                var dts = dl.children('dt');
                var panes = dl.children('dd');
                var groups = new Array(dts, panes);

                //Create a ul for tabs if necessary.
                if (hasTabs) {
                    var ul = jQuery('<ul class="toggle-tabs"></ul>');
                    dts.each(function () {
                        var dt = jQuery(this);
                        var li = jQuery('<li></li>');
                        li.html(dt.html());
                        ul.append(li);
                    });
                    ul.insertBefore(dl);
                    var lis = ul.children();
                    groups.push(lis);
                }

                //Add "last" classes.
                var i;
                for (i = 0; i < groups.length; i++) {
                    groups[i].filter(':last').addClass('last');
                }

                function toggleClasses(clickedItem, group) {
                    var index = group.index(clickedItem);
                    var i;
                    for (i = 0; i < groups.length; i++) {
                        groups[i].removeClass('current');
                        groups[i].eq(index).addClass('current');
                    }
                }

                //Toggle on tab (dt) click.
                dts.on('click', function (e) {
                    //They clicked the current dt to close it. Restore the wrapper to unclicked state.
                    if (jQuery(this).hasClass('current') && wrapper.hasClass('accordion-open')) {
                        wrapper.removeClass('accordion-open');
                    } else {
                        //They're clicking something new. Reflect the explicit user interaction.
                        wrapper.addClass('accordion-open');
                    }
                    toggleClasses(jQuery(this), dts);
                });

                //Toggle on tab (li) click.
                if (hasTabs) {
                    lis.on('click', function (e) {
                        toggleClasses(jQuery(this), lis);
                    });
                    //Open the first tab.
                    lis.eq(0).trigger('click');
                }

                //Open the first accordion if desired.
                if (startOpen) {
                    dts.eq(0).trigger('click');
                }
            });
        }
    });

    module.views("PageProductDetailsView", PageProductDetailsView);
});

