MagentoApp.module("uma_website", function(module, app, Backbone, Marionette, $j, _) {

    var LayoutView = Marionette.LayoutView,
        _super = LayoutView.prototype;

    var PageMobileNavigation = LayoutView.extend({

        initialize: function(options) {
            _super.initialize.apply(this, arguments);
            this.bindUIEvents();
        },

        /**
         * bind custom UI events for user interaction on mobile and desktop
         **/
        bindUIEvents: function() {

            this.$el.on("click", ".btn-close, .overlay", function () {
                app.commands.execute("toggle-mobile-navigation");
            });

            this.$("#cssmenu").menumaker();
        }
    });

    module.views("PageMobileNavigation", PageMobileNavigation);
});

