MagentoApp.module("uma_website", function(module, app, Backbone, Marionette, $j, _) {

    // reference main components
    var PageHeaderView = module.views("PageHeaderView"),
        PageBodyView   = module.views("PageBodyView"),
        PageFooterView = module.views("PageFooterView")
        PageMBNav      = module.views("PageMobileNavigation");

    // register some custom handlers for events triggered by the application
    app.addInitializer(
        function(options) {
            // Set up the handler to call open Zopim chat support window
            this.commands.setHandler("toggle-chat-support-window", function(mode){
                // available through Zopim API
                mode == "show"
                    ? $zopim.livechat.window.show()
                    : $zopim.livechat.window.hide();
            });

            // Set up the handler to toggle display off-canvas-navigation
            this.commands.setHandler("toggle-mobile-navigation", function(mode) {
                // toggle diplay off-canvas menu on the left
                Backbone.$("body").toggleClass("mn-open")
                    .find(".mobile-navigation").toggleClass("open");
            });
        }
    );

    // trigger app to show tracked components
    app.addInitializer(
        function(options) {

            // backup application context
            var self = this;

            // bootstrap right after DOM is ready
            Backbone.$(document).ready(function() {

                if (self.is_mobile) {
                    Backbone.$("body").addClass("mobile");
                }

                // keep track of view components
                _.extend(app.views, {

                    "header": new PageHeaderView({
                        el: self.page_components.page_header
                    }),

                    "body": new PageBodyView({
                        el: self.page_components.page_body
                    }),

                    "footer": new PageFooterView({
                        el: self.page_components.page_footer
                    }),

                    "mobile_navigation": new PageMBNav({
                        el: self.page_components.page_mb_nav
                    })
                });
            });
        }
    );

    /** register handler to work with ajax add to cart */
    app.addInitializer(
        function(options) {
            /**
             * dynamically load shopping cart popup
             */
            this.loadShoppingCart = function(url) {
                var $def = $j.Deferred();
                try {
                    $j.ajax({
                        "url": url,
                        "method": "POST",
                        "dataType": "json",
                    }).then(
                        function(response) {
                            $def.resolve(fragmentFromString(response["html"]));
                        },
                        function(xhr, error, obj) {
                            $def.reject(xhr, error, obj);
                        }
                    );
                }
                catch(e) {
                    $def.reject(e);
                }
                return $def.promise();
            }

            /**
             * show and reload shopping cart popup
             */
            this.showShoppingCartPopup = function(url) {
                // console.info("showing shopping cart");
                var $popups = $j(".popups-container .popup");
                var $cart_popup = $j(".popups-container .popup.cart");
                $popups.removeClass("active");

                this.loadShoppingCart(url).then(
                    function(fragment) {
                        $cart_popup.find(".contents").empty().append(fragment);
                    },
                    function(xhr, error, obj) {
                        //console.error(error);
                    }
                ).always(
                    function() {
                        $cart_popup.addClass("active");
                    }
                );
            }
        }
    );

    /** register handler to work with ajax add to wishlist */
    app.addInitializer(
        function(options) {
            /**
             * dynamically load wishlist popup
             */
            this.loadWishlist = function(url) {
                var $def = $j.Deferred();
                try {
                    $j.ajax({
                        "url": url,
                        "method": "POST",
                        "dataType": "json",
                        "data": {
                            /* by pass preDispatch in wishlist controller */
                            "is_ajax": true,
                        }
                    }).then(
                        function(response) {
                            $def.resolve(fragmentFromString(response["html"]));
                        },
                        function(xhr, error, obj) {
                            $def.reject(xhr, error, obj);
                        }
                    );
                }
                catch(e) {
                    $def.reject(e);
                }
                return $def.promise();
            }
            /**
             * show and reload wishlist popup
             * @param url {string}: the url of the cart
             */
            this.showWishlistPopup = function(url) {
                var $popups = $j(".popups-container .popup");
                var $wishlist_popup = $j(".popups-container .popup.wishlist");
                $popups.removeClass("active");

                this.loadWishlist(url).then(
                    function(fragment) {
                        $wishlist_popup.find(".contents").empty().append(fragment);
                    },
                    function(xhr, error, obj) {
                        //console.error(error);
                    }
                ).always(
                    function() {
                        $wishlist_popup.addClass("active");
                    }
                );
            }
        }
    );
});



